# Given a string representing a space separated lowercase letters, return a hash
# of the letter with the most repetition and containing the corresponding count.
# If several letters have the same occurrence, return all of them.
# Example:
# >>> histogram.call("a b c")
# {"a" => 1, "b" => 1, "c" => 1}
# >>> histogram.call("a b b a")
# {"a" => 2, "b" => 2}
# >>> histogram.call("a b c a b")
# {"a" => 2, "b" => 2}
# >>> histogram.call("b b b b a")
# {"b" => 4}
# >>> histogram.call("")
# {}
def histogram(test)
  counts = Hash.new(0)
  test.split.each { |char| counts[char] += 1 }
  max_count = counts.values.max
  counts.select { |k, v| v == max_count }
end

# Input is a space-delimited string of numberals from 'zero' to 'nine'.
# Valid choices are 'zero', 'one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight' and 'nine'.
# Return the string with numbers sorted from smallest to largest
# >>> sort_numbers.call("three one five")
# "one three five"
def sort_numbers(numbers)
  number_words = {
    'zero' => 0, 'one' => 1, 'two' => 2, 'three' => 3, 'four' => 4,
    'five' => 5, 'six' => 6, 'seven' => 7, 'eight' => 8, 'nine' => 9
  }

  sorted_numbers = numbers.split(' ').sort_by { |word| number_words[word] }
  sorted_numbers.join(' ')
end
